                return new KZ_Loading(config);
            }
        }

        function SaveChanges() {
            if (!_status) {
                return false;
            }
            var loading = new KZ_Loading('正在保存中...');
            _status = false;
            loading.show();
            $.ajax({
                url: '/api/save_post/',
                method: 'post',
                data: {"file": "{{ fullname }}", "content": vditor.getValue()},
                dataType: 'JSON',
                success: function (res) {
                    loading.destroy();
                    if (res.status) {
                        $.notify('保存成功！', {type: "success", delay: 1000});
                        _status = true;
                    } else {
                        $.notify(res.msg, {type: "warning", delay: 1000});
                        _status = true;
                    }
                },
                error: function (res) {
                    loading.destroy();
                    $.notify("网络错误！", {type: "warning", delay: 1000});
                    _status = true;
                }
            })
        }

        function SaveDraft() {
            if (!_status) {
                return false;
            }
            var loading = new KZ_Loading('草稿保存中...');
            _status = false;
            loading.show();
